body.script:
document.addEventListener("DOMContentLoaded", function() {
  const svg = document.querySelector('svg');

  // Access the SVG layers (Z-layers)
  const layer1 = svg.querySelector('#layer1');
  const layer2 = svg.querySelector('#layer2');
  const layer3 = svg.querySelector('#layer3');

  // Initial setup: Only show layer1 and layer3, hide layer2
  layer1.style.display = 'block';
  layer2.style.display = 'none';
  layer3.style.display = 'block';

  // Function to toggle layers
  function toggleLayers() {
    // Toggle visibility of layers (for demonstration purposes)
    if (layer1.style.display === 'block') {
      layer1.style.display = 'none';
      layer2.style.display = 'block'; // Show layer 2
    } else {
      layer1.style.display = 'block';
      layer2.style.display = 'none'; // Hide layer 2
    }
  }

  // Change layers every 3 seconds for demonstration
  setInterval(toggleLayers, 3000);
});
